mapper.update:
UPDATE QNA SET
	category = #{category}
	,question_title = #{title}
	,question_content = #{content}
WHERE QNA_ID = #{qnaId}

UPDATE QNA SET
	views = IFNULL(views,0)+1
WHERE QNA_ID = #{qnaId}

UPDATE QNA SET
	like_count = IFNULL(like_count,0) + #{likeCount}
WHERE QNA_ID = #{qnaId}

UPDATE QNA SET
	answer_title = #{answerTitle}
	,answer_content = #{answerContent}
	,answer_date = now()
WHERE QNA_ID = #{qnaId}

UPDATE QNA SET
	delete_yn = 'Y'
WHERE QNA_ID = #{qnaId}
